CREATE FUNCTION public.check_resim_urls_format() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
begin
  -- JSON array olduğunu kontrol et
  if not jsonb_typeof(new.resim_urls) = 'array' then
    raise exception 'resim_urls bir dizi olmalıdır';
  end if;

  -- Her elemanın name ve url alanlarına sahip olduğunu kontrol et
  if exists (
    select 1
    from jsonb_array_elements(new.resim_urls) as elem
    where not (
      jsonb_typeof(elem -> 'name') = 'string' and
      jsonb_typeof(elem -> 'url') = 'string'
    )
  ) then
    raise exception 'Her resim kaydı name ve url alanlarına sahip olmalıdır';
  end if;

  return new;
end;
$$;


