--
-- Name: create_gider_movement(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.create_gider_movement() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    gider_kasa_id UUID;
BEGIN
    -- Gider için kasa belirle
    IF NEW.kasa_id IS NOT NULL THEN
        gider_kasa_id := NEW.kasa_id;
    ELSIF NEW.banka_id IS NOT NULL THEN
        -- Banka ödemesi için ilgili banka kasasını bul
        SELECT id INTO gider_kasa_id 
        FROM public.kasalar 
        WHERE banka_id = NEW.banka_id AND aktif = true 
        LIMIT 1;
    ELSE
        -- Varsayılan nakit kasayı kullan
        SELECT id INTO gider_kasa_id 
        FROM public.kasalar 
        WHERE kasa_tipi = 'nakit' AND aktif = true 
        ORDER BY olusturma_tarihi ASC 
        LIMIT 1;
    END IF;
    
    -- Eğer uygun kasa bulunamazsa varsayılan kasayı kullan
    IF gider_kasa_id IS NULL THEN
        SELECT id INTO gider_kasa_id 
        FROM public.kasalar 
        WHERE aktif = true 
        ORDER BY olusturma_tarihi ASC 
        LIMIT 1;
    END IF;
    
    -- Eğer hiç kasa yoksa hata ver
    IF gider_kasa_id IS NULL THEN
        RAISE EXCEPTION 'Gider işlemi için aktif bir kasa bulunamadı. Lütfen önce bir kasa tanımlayın.';
    END IF;
    
    -- Kasa hareketini oluştur
    INSERT INTO public.kasa_hareketleri (
        kasa_id,
        hareket_tipi,
        tutar,
        aciklama,
        belge_tipi,
        belge_no,
        referans_id,
        islem_tarihi
    ) VALUES (
        gider_kasa_id,
        'cikis',
        NEW.toplam_tutar,
        'Gider - ' || NEW.gider_adi,
        'gider',
        NEW.gider_no,
        NEW.id,
        NEW.gider_tarihi::timestamp
    );
    
    RETURN NEW;
END;
$$;


