            ) VALUES (
                item_record.stok_id,
                'giris',
                item_record.miktar,
                item_record.eldeki_miktar,
                item_record.eldeki_miktar + item_record.miktar,
                item_record.birim_fiyat,
                item_record.toplam_tutar,
                'İade - ' || NEW.iade_no || ' - ' || item_record.stok_adi,
                'iade_faturasi',
                NEW.iade_no,
                NEW.id
            );
            
            -- Stok miktarını artır
            UPDATE public.stoklar 
            SET eldeki_miktar = eldeki_miktar + item_record.miktar,
                guncelleme_tarihi = now()
            WHERE id = item_record.stok_id;
        END LOOP;
        
        -- Cari hesap hareketi oluştur (eğer cari hesap varsa)
        IF NEW.cari_hesap_id IS NOT NULL THEN
            -- Mevcut bakiyeyi al
            SELECT bakiye INTO current_balance 
            FROM public.cari_hesaplar 
            WHERE id = NEW.cari_hesap_id;
            
            -- Cari hesap hareketini oluştur (iade alacak yaratır)
            INSERT INTO public.cari_hesap_hareketleri (
                cari_hesap_id,
                hareket_tipi,
                tutar,
                onceki_bakiye,
                sonraki_bakiye,
                aciklama,
                belge_tipi,
                belge_no,
                referans_id,
                islem_tarihi
            ) VALUES (
                NEW.cari_hesap_id,
                'alacak',
                NEW.toplam_tutar,
                current_balance,
                current_balance - NEW.toplam_tutar,
                'İade Faturası - ' || NEW.iade_no,
                'iade_faturasi',
                NEW.iade_no,
                NEW.id,
                NEW.iade_tarihi::timestamp
            );
            
            -- Cari hesap bakiyesini güncelle
            UPDATE public.cari_hesaplar 
            SET bakiye = current_balance - NEW.toplam_tutar,
                guncelleme_tarihi = now()
            WHERE id = NEW.cari_hesap_id;
        END IF;
    END IF;
    
    RETURN NEW;
END;
$$;


--
-- Name: create_sale_with_movements(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.create_sale_with_movements() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    item_record RECORD;
    current_balance NUMERIC(15,2) := 0;
    kasa_record RECORD;
    kasa_current_balance NUMERIC(15,2) := 0;
BEGIN
    -- Manuel onay işlemi kontrolü
    IF NEW.notlar IS NOT NULL AND NEW.notlar LIKE '%MANUAL_APPROVAL%' THEN
        RETURN NEW;
    END IF;

    -- Satış tamamlandığında
    IF NEW.satis_durumu = 'tamamlandi' AND (TG_OP = 'INSERT' OR (OLD IS NULL OR OLD.satis_durumu IS NULL OR OLD.satis_durumu != 'tamamlandi')) THEN
        
        -- Stok işlemleri
        FOR item_record IN 
            SELECT sk.*, s.stok_adi, s.eldeki_miktar 
            FROM public.satis_kalemleri sk
            JOIN public.stoklar s ON sk.stok_id = s.id
            WHERE sk.satis_id = NEW.id
        LOOP
            -- Stok yeterli mi kontrol et
            IF item_record.eldeki_miktar < item_record.miktar THEN
                RAISE EXCEPTION 'Yetersiz stok: % için mevcut: %, istenen: %', 
                    item_record.stok_adi, item_record.eldeki_miktar, item_record.miktar;
            END IF;
            
            -- Stok hareketi oluştur
            INSERT INTO public.stok_hareketleri (
                stok_id, hareket_tipi, miktar, onceki_miktar, sonraki_miktar,
                birim_fiyat, toplam_tutar, aciklama, belge_tipi, belge_no, referans_id
            ) VALUES (
                item_record.stok_id, 'cikis', item_record.miktar,
                item_record.eldeki_miktar, item_record.eldeki_miktar - item_record.miktar,
                item_record.birim_fiyat, item_record.toplam_tutar,
                'Satış - ' || NEW.satis_no || ' - ' || item_record.stok_adi,
                'satis', NEW.satis_no, NEW.id
            );
            
            -- Stok güncelle
            UPDATE public.stoklar 
            SET eldeki_miktar = eldeki_miktar - item_record.miktar, guncelleme_tarihi = now()
            WHERE id = item_record.stok_id;
        END LOOP;
        
        -- Cari hesap işlemleri (sadece müşteri varsa)
        IF NEW.cari_hesap_id IS NOT NULL THEN
            SELECT COALESCE(bakiye, 0) INTO current_balance 
            FROM public.cari_hesaplar WHERE id = NEW.cari_hesap_id;
            
            -- Sadece veresiye satışlarda bakiyeye ekle
            IF NEW.odeme_sekli = 'veresiye' THEN
                INSERT INTO public.cari_hesap_hareketleri (
                    cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
                    aciklama, belge_tipi, belge_no, referans_id, islem_tarihi
                ) VALUES (
                    NEW.cari_hesap_id, 'borc', NEW.toplam_tutar, current_balance, current_balance + NEW.toplam_tutar,
                    'Satış - ' || NEW.satis_no, 'satis', NEW.satis_no, NEW.id, NOW()
                );
                
                UPDATE public.cari_hesaplar 
                SET bakiye = current_balance + NEW.toplam_tutar, guncelleme_tarihi = now()
                WHERE id = NEW.cari_hesap_id;
            ELSE
                -- Diğer ödeme şekillerinde sadece 'satis' hareket kaydı (bakiye değişmez)
                INSERT INTO public.cari_hesap_hareketleri (
                    cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
                    aciklama, belge_tipi, belge_no, referans_id, islem_tarihi
                ) VALUES (
                    NEW.cari_hesap_id, 'satis', NEW.toplam_tutar, current_balance, current_balance,
                    'Satış - ' || NEW.satis_no || ' (' || NEW.odeme_sekli || ')', 
                    'satis', NEW.satis_no, NEW.id, NOW()
                );
            END IF;
        END IF;
        
        -- Kasa hareketleri oluştur
        IF NEW.odeme_sekli IN ('nakit', 'kredi_karti', 'havale', 'cek', 'senet') THEN
            -- Ödeme tipine göre kasa seç
            IF NEW.odeme_sekli = 'nakit' THEN
                SELECT * INTO kasa_record FROM public.kasalar WHERE kasa_tipi = 'nakit' AND aktif = true LIMIT 1;
            ELSIF NEW.odeme_sekli IN ('kredi_karti', 'havale') THEN
                IF NEW.banka_id IS NOT NULL THEN
                    SELECT * INTO kasa_record FROM public.kasalar WHERE banka_id = NEW.banka_id AND aktif = true LIMIT 1;
                END IF;
                IF kasa_record IS NULL THEN
                    SELECT * INTO kasa_record FROM public.kasalar WHERE kasa_tipi = 'banka' AND aktif = true LIMIT 1;
                END IF;
            ELSIF NEW.odeme_sekli = 'cek' THEN
                SELECT * INTO kasa_record FROM public.kasalar WHERE kasa_tipi = 'cek' AND aktif = true LIMIT 1;
            ELSIF NEW.odeme_sekli = 'senet' THEN
                SELECT * INTO kasa_record FROM public.kasalar WHERE kasa_tipi = 'senet' AND aktif = true LIMIT 1;
            END IF;
            
            -- Eğer uygun kasa bulunamazsa, varsayılan olarak nakit kasasını kullan
            IF kasa_record IS NULL THEN
                SELECT * INTO kasa_record FROM public.kasalar WHERE kasa_tipi = 'nakit' AND aktif = true LIMIT 1;
            END IF;

            IF kasa_record IS NOT NULL THEN
                -- Kasa mevcut bakiyesini al
                SELECT COALESCE(guncel_bakiye, 0) INTO kasa_current_balance 
                FROM public.kasalar 
                WHERE id = kasa_record.id;
                
                -- Kasa hareketi oluştur
                INSERT INTO public.kasa_hareketleri (
                    kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, aciklama, 
                    belge_tipi, belge_no, referans_id, islem_tarihi, olusturma_tarihi
                ) VALUES (
                    kasa_record.id, 'giris', NEW.toplam_tutar, kasa_current_balance,
                    kasa_current_balance + NEW.toplam_tutar,
                    'Satış - ' || NEW.satis_no || ' (' || NEW.odeme_sekli || ')',
                    'satis', NEW.satis_no, NEW.id, NOW(), NOW()
                );
                
                -- Kasa bakiyesini güncelle
                UPDATE public.kasalar 
                SET guncel_bakiye = kasa_current_balance + NEW.toplam_tutar,
                    guncelleme_tarihi = now()
                WHERE id = kasa_record.id;
            END IF;
        END IF;
        
    END IF;
    
    RETURN NEW;
END;
$$;


