-- Name: create_todo_notification(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.create_todo_notification() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    -- Yeni görev eklendiğinde bildirim oluştur
    IF TG_OP = 'INSERT' THEN
        INSERT INTO public.notifications (baslik, mesaj, tip, todo_id)
        VALUES (
            'Yeni Görev Eklendi',
            'Yeni görev "' || NEW.baslik || '" oluşturuldu.',
            'bilgi',
            NEW.id
        );
        RETURN NEW;
    END IF;
    
    -- Görev tamamlandığında bildirim oluştur
    IF TG_OP = 'UPDATE' AND OLD.tamamlandi = false AND NEW.tamamlandi = true THEN
        INSERT INTO public.notifications (baslik, mesaj, tip, todo_id)
        VALUES (
            'Görev Tamamlandı',
            '"' || NEW.baslik || '" görevi başarıyla tamamlandı.',
            'basari',
            NEW.id
        );
        RETURN NEW;
    END IF;
    
    -- Bugün bitiş tarihi olan görevler için uyarı (günlük kontrol)
    IF TG_OP = 'UPDATE' AND NEW.bitis_tarihi = CURRENT_DATE AND NEW.tamamlandi = false THEN
        INSERT INTO public.notifications (baslik, mesaj, tip, todo_id)
        VALUES (
            'Görev Hatırlatması',
            '"' || NEW.baslik || '" görevinin bitiş tarihi bugün!',
            'uyari',
            NEW.id
        );
        RETURN NEW;
    END IF;
    
    RETURN NEW;
END;
$$;


--
-- Name: delete_alis_faturasi(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.delete_alis_faturasi(p_alis_id uuid) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_alis RECORD;
  v_kalem RECORD;
  v_stok_guncelleme_sayisi INTEGER := 0;
BEGIN
  -- 1. Alış bilgilerini al
  SELECT * INTO v_alis
  FROM alislar
  WHERE id = p_alis_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Alış faturası bulunamadı: %', p_alis_id;
  END IF;
  
  -- 2. Her alış kalemi için stokları düşür (alış iptal edildiği için stok AZALIR)
  FOR v_kalem IN 
    SELECT stok_id, miktar 
    FROM alis_kalemleri 
    WHERE alis_id = p_alis_id
  LOOP
    -- Stok miktarını azalt (negatif olabilir)
    UPDATE stoklar
    SET eldeki_miktar = eldeki_miktar - v_kalem.miktar
    WHERE id = v_kalem.stok_id;
    
    -- Güncelleme sayısını artır
    IF FOUND THEN
      v_stok_guncelleme_sayisi := v_stok_guncelleme_sayisi + 1;
      
      RAISE NOTICE 'Stok güncellendi: % - Miktar azaltıldı: %', v_kalem.stok_id, v_kalem.miktar;
    END IF;
  END LOOP;
  
  -- 3. Stok hareketlerini sil
  DELETE FROM stok_hareketleri
  WHERE referans_id = p_alis_id
    AND belge_tipi = 'alis';
  
  -- 4. Cari hesap hareketini sil
  DELETE FROM cari_hesap_hareketleri
  WHERE referans_id = p_alis_id
    AND belge_tipi = 'alis';
  
  -- 5. Alış kalemlerini sil
  DELETE FROM alis_kalemleri
  WHERE alis_id = p_alis_id;
  
  -- 6. Alışı sil
  DELETE FROM alislar
  WHERE id = p_alis_id;
  
  -- 7. Tedarikçi bakiyesini yeniden hesapla
  IF v_alis.tedarikci_id IS NOT NULL THEN
    PERFORM recalculate_cari_bakiye(v_alis.tedarikci_id);
  END IF;
  
  -- Başarı mesajı döndür
  RETURN jsonb_build_object(
    'success', true,
    'message', 'Alış faturası başarıyla silindi',
    'fatura_no', v_alis.fatura_no,
    'toplam_tutar', v_alis.toplam_tutar,
    'stok_guncelleme_sayisi', v_stok_guncelleme_sayisi
  );
  
EXCEPTION
  WHEN OTHERS THEN
    RAISE EXCEPTION 'Alış faturası silinirken hata: %', SQLERRM;
END;
$$;


