--
-- Name: delete_iade_faturasi(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.delete_iade_faturasi(p_iade_id uuid) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE 
  v_iade RECORD;
  v_stok_hareket RECORD;
  v_geri_alinan_stok_sayisi INT := 0;
BEGIN
  SELECT * INTO v_iade FROM iadeler WHERE id = p_iade_id;
  IF NOT FOUND THEN 
    RAISE EXCEPTION 'İade faturası bulunamadı: %', p_iade_id; 
  END IF;
  
  -- İade girişlerini geri al (stok azalt)
  FOR v_stok_hareket IN 
    SELECT sh.*, s.stok_kodu, s.eldeki_miktar
    FROM stok_hareketleri sh
    LEFT JOIN stoklar s ON s.id = sh.stok_id
    WHERE sh.referans_id = p_iade_id
  LOOP
    IF v_stok_hareket.belge_tipi IN ('iade', 'iade_faturasi') 
       AND v_stok_hareket.hareket_tipi = 'giris' THEN
      
      UPDATE stoklar 
      SET eldeki_miktar = eldeki_miktar - v_stok_hareket.miktar
      WHERE id = v_stok_hareket.stok_id;
      
      v_geri_alinan_stok_sayisi := v_geri_alinan_stok_sayisi + 1;
      RAISE NOTICE '✅ İade stoku geri alındı: %', v_stok_hareket.stok_kodu;
    END IF;
  END LOOP;
  
  DELETE FROM stok_hareketleri WHERE referans_id = p_iade_id;
  DELETE FROM cari_hesap_hareketleri WHERE referans_id = p_iade_id;
  DELETE FROM iade_kalemleri WHERE iade_id = p_iade_id;
  DELETE FROM iadeler WHERE id = p_iade_id;
  
  IF v_iade.cari_hesap_id IS NOT NULL THEN
    PERFORM recalculate_cari_bakiye(v_iade.cari_hesap_id);
  END IF;
  
  RETURN jsonb_build_object(
    'success', true, 
    'message', format('İade faturası başarıyla silindi. %s stok geri alındı.', v_geri_alinan_stok_sayisi),
    'iade_no', v_iade.iade_no, 
    'toplam_tutar', v_iade.toplam_tutar,
    'geri_alinan_stok_sayisi', v_geri_alinan_stok_sayisi
  );
END;
$$;


