-- Name: FUNCTION delete_iade_faturasi(p_iade_id uuid); Type: COMMENT; Schema: public; Owner: -
--

COMMENT ON FUNCTION public.delete_iade_faturasi(p_iade_id uuid) IS 'İade faturasını siler, stok ve cari bakiye otomatik güncellenir.';


--
-- Name: delete_satis_faturasi(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.delete_satis_faturasi(p_satis_id uuid) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE 
  v_satis RECORD;
  v_stok_hareket RECORD;
  v_geri_alinan_stok_sayisi INT := 0;
  v_silinen_hareket_sayisi INT := 0;
BEGIN
  -- 1. Fatura bilgilerini al
  SELECT * INTO v_satis FROM satislar WHERE id = p_satis_id;
  IF NOT FOUND THEN 
    RAISE EXCEPTION 'Satış faturası bulunamadı: %', p_satis_id; 
  END IF;
  
  RAISE NOTICE 'Satış siliniyor: % (ID: %)', v_satis.satis_no, p_satis_id;
  
  -- 2. ÖNCE stok hareketlerini bul ve stokları geri al
  FOR v_stok_hareket IN 
    SELECT sh.*, s.stok_kodu, s.stok_adi, s.eldeki_miktar
    FROM stok_hareketleri sh
    LEFT JOIN stoklar s ON s.id = sh.stok_id
    WHERE sh.referans_id = p_satis_id
  LOOP
    RAISE NOTICE 'Stok hareketi bulundu: Stok=%, Miktar=%, Belge Tipi=%, Hareket Tipi=%', 
      v_stok_hareket.stok_kodu, v_stok_hareket.miktar, 
      v_stok_hareket.belge_tipi, v_stok_hareket.hareket_tipi;
    
    -- Sadece satış çıkışlarını geri al
    IF v_stok_hareket.belge_tipi IN ('satis', 'satis_faturasi') 
       AND v_stok_hareket.hareket_tipi = 'cikis' THEN
      
      -- Stoku geri ekle
      UPDATE stoklar 
      SET eldeki_miktar = eldeki_miktar + v_stok_hareket.miktar
      WHERE id = v_stok_hareket.stok_id;
      
      v_geri_alinan_stok_sayisi := v_geri_alinan_stok_sayisi + 1;
      
      RAISE NOTICE '✅ Stok geri alındı: % - Eski: %, Eklenen: %, Yeni: %',
        v_stok_hareket.stok_kodu,
        v_stok_hareket.eldeki_miktar,
        v_stok_hareket.miktar,
        v_stok_hareket.eldeki_miktar + v_stok_hareket.miktar;
    ELSE
      RAISE NOTICE '⚠️ Atlandı (belge_tipi=% hareket_tipi=%)', 
        v_stok_hareket.belge_tipi, v_stok_hareket.hareket_tipi;
    END IF;
  END LOOP;
  
  -- 3. Stok hareketlerini sil
  DELETE FROM stok_hareketleri 
  WHERE referans_id = p_satis_id;
  
  GET DIAGNOSTICS v_silinen_hareket_sayisi = ROW_COUNT;
  RAISE NOTICE 'Silinen stok hareketi sayısı: %', v_silinen_hareket_sayisi;
  
  -- 4. Cari hesap hareketini sil
  DELETE FROM cari_hesap_hareketleri 
  WHERE referans_id = p_satis_id;
  
  -- 5. Fatura kalemlerini sil
  DELETE FROM satis_kalemleri 
  WHERE satis_id = p_satis_id;
  
  -- 6. Faturayı sil
  DELETE FROM satislar 
  WHERE id = p_satis_id;
  
  -- 7. Cari bakiyeyi yeniden hesapla
  IF v_satis.cari_hesap_id IS NOT NULL THEN
    PERFORM recalculate_cari_bakiye(v_satis.cari_hesap_id);
  END IF;
  
  RAISE NOTICE '✅ Satış başarıyla silindi. Geri alınan stok sayısı: %', v_geri_alinan_stok_sayisi;
  
  RETURN jsonb_build_object(
    'success', true, 
    'message', format('Satış faturası başarıyla silindi. %s stok geri alındı.', v_geri_alinan_stok_sayisi),
    'satis_no', v_satis.satis_no, 
    'toplam_tutar', v_satis.toplam_tutar,
    'geri_alinan_stok_sayisi', v_geri_alinan_stok_sayisi,
    'silinen_hareket_sayisi', v_silinen_hareket_sayisi
  );
  
EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Satış faturası silinirken hata: %', SQLERRM;
END;
$$;


