-- Name: delete_tahsilat(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.delete_tahsilat(p_tahsilat_id uuid) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_tahsilat RECORD;
BEGIN
  -- 1. Tahsilat bilgilerini al
  SELECT * INTO v_tahsilat
  FROM tahsilatlar
  WHERE id = p_tahsilat_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Tahsilat bulunamadı: %', p_tahsilat_id;
  END IF;
  
  -- 2. Cari hesap hareketini sil
  DELETE FROM cari_hesap_hareketleri
  WHERE referans_id = p_tahsilat_id
    AND belge_tipi = 'tahsilat';
  
  -- 3. Tahsilatı sil
  DELETE FROM tahsilatlar
  WHERE id = p_tahsilat_id;
  
  -- 4. Cari bakiyeyi yeniden hesapla
  IF v_tahsilat.cari_hesap_id IS NOT NULL THEN
    PERFORM recalculate_cari_bakiye(v_tahsilat.cari_hesap_id);
  END IF;
  
  -- 5. Silme logunu kaydet
  INSERT INTO belge_degisiklik_loglari (
    belge_id, belge_tipi, islem_tipi,
    eski_veri, degisiklik_aciklamasi
  ) VALUES (
    p_tahsilat_id,
    'tahsilat',
    'silme',
    jsonb_build_object(
      'tahsilat_no', v_tahsilat.tahsilat_no,
      'tutar', v_tahsilat.tutar,
      'tahsilat_tarihi', v_tahsilat.tahsilat_tarihi,
