--
-- Name: generate_satis_no(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.generate_satis_no() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_date_part TEXT;
  v_sequence INT;
  v_new_satis_no TEXT;
BEGIN
  -- Eğer satis_no zaten doluysa, trigger'ı atla
  IF NEW.satis_no IS NOT NULL AND NEW.satis_no != '' AND NEW.satis_no != 'null' THEN
    RETURN NEW;
  END IF;

  -- Tarih kısmını oluştur (YYYYMMDD formatında)
  v_date_part := TO_CHAR(COALESCE(NEW.satis_tarihi, CURRENT_DATE), 'YYYYMMDD');

  -- Aynı gün için son sıra numarasını bul
  SELECT COALESCE(MAX(
    CAST(
      SUBSTRING(satis_no FROM 'SAT-[0-9]{8}-([0-9]+)') AS INTEGER
    )
  ), 0) + 1
  INTO v_sequence
  FROM satislar
  WHERE satis_no LIKE 'SAT-' || v_date_part || '-%';

  -- Yeni satis_no oluştur
  v_new_satis_no := 'SAT-' || v_date_part || '-' || LPAD(v_sequence::TEXT, 3, '0');

  -- Unique olduğundan emin ol (çakışma varsa sequence'i artır)
  WHILE EXISTS (SELECT 1 FROM satislar WHERE satis_no = v_new_satis_no) LOOP
    v_sequence := v_sequence + 1;
    v_new_satis_no := 'SAT-' || v_date_part || '-' || LPAD(v_sequence::TEXT, 3, '0');
  END LOOP;

  NEW.satis_no := v_new_satis_no;
  RETURN NEW;
END;
$$;


