--
-- Name: recalculate_cari_bakiye(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.recalculate_cari_bakiye(p_cari_hesap_id uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_hareket RECORD;
  v_running_balance NUMERIC := 0;
  v_previous_balance NUMERIC := 0;
BEGIN
  -- 1. Tüm hareketleri islem_tarihi'ne göre sırala ve bakiyeleri yeniden hesapla
  FOR v_hareket IN 
    SELECT id, hareket_tipi, tutar, islem_tarihi
    FROM cari_hesap_hareketleri
    WHERE cari_hesap_id = p_cari_hesap_id
    ORDER BY islem_tarihi ASC, olusturma_tarihi ASC, id ASC
  LOOP
    -- Önceki bakiye
    v_previous_balance := v_running_balance;
    
    -- Yeni bakiye hesapla
    IF v_hareket.hareket_tipi = 'borc' THEN
      v_running_balance := v_running_balance + v_hareket.tutar;
    ELSIF v_hareket.hareket_tipi = 'alacak' THEN
      v_running_balance := v_running_balance - v_hareket.tutar;
    ELSIF v_hareket.hareket_tipi = 'satis' THEN
      -- Peşin satış - bakiye değişmez
      v_running_balance := v_running_balance;
    END IF;
    
    -- Hareketi güncelle
    UPDATE cari_hesap_hareketleri
    SET 
      onceki_bakiye = v_previous_balance,
      sonraki_bakiye = v_running_balance,
      guncelleme_tarihi = NOW()
    WHERE id = v_hareket.id;
  END LOOP;
  
  -- 2. Cari hesabın son bakiyesini güncelle
  UPDATE cari_hesaplar
  SET 
    bakiye = v_running_balance,
    guncelleme_tarihi = NOW()
  WHERE id = p_cari_hesap_id;
  
  RAISE NOTICE 'Recalculated balance for cari_hesap %: final balance = %', p_cari_hesap_id, v_running_balance;
END;
$$;


