--
-- Name: satis_trigger_function(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.satis_trigger_function() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_odeme_sekli TEXT;
  v_banka_id UUID;
  v_cek_no TEXT;
  v_senet_no TEXT;
  v_vade_tarihi DATE;
  v_dekont_no TEXT;
  v_manual_approval BOOLEAN;
  item RECORD;
BEGIN
  -- Manuel onay kontrolü
  v_manual_approval := NEW.notlar LIKE '%MANUAL_APPROVAL%';
  
  IF v_manual_approval THEN
    RETURN NEW;
  END IF;
  
  -- Ödeme detaylarını al
  v_odeme_sekli := NEW.odeme_sekli;
  v_banka_id := NEW.banka_id;
  v_cek_no := NEW.cek_no;
  v_senet_no := NEW.senet_no;
  v_vade_tarihi := NEW.vade_tarihi;
  v_dekont_no := NEW.dekont_no;
  
  -- Stok işlemleri
  FOR item IN SELECT * FROM satis_kalemleri WHERE satis_id = NEW.id LOOP
    -- Stok miktarını güncelle
    UPDATE stoklar 
    SET eldeki_miktar = eldeki_miktar - item.miktar,
        guncelleme_tarihi = NOW()
    WHERE id = item.stok_id;
    
    -- Stok hareketi oluştur
    INSERT INTO stok_hareketleri (
      stok_id, hareket_tipi, miktar, birim_fiyat, toplam_tutar,
      aciklama, belge_tipi, belge_no, referans_id
    ) VALUES (
      item.stok_id, 'cikis', item.miktar, item.birim_fiyat, item.toplam_tutar,
      'Satış - ' || NEW.satis_no, 'satis', NEW.satis_no, NEW.id
    );
  END LOOP;
  
  -- Kasa işlemleri
  IF v_odeme_sekli IN ('nakit', 'kredi_karti', 'banka_transferi', 'cek', 'senet') THEN
    DECLARE
      v_kasa_id UUID;
      v_kasa_bakiye DECIMAL;
    BEGIN
      -- Kasa seçimi
      IF v_odeme_sekli = 'nakit' THEN
        SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
        FROM kasalar WHERE kasa_tipi = 'nakit' AND aktif = true LIMIT 1;
      ELSIF v_odeme_sekli IN ('kredi_karti', 'banka_transferi') THEN
        IF v_banka_id IS NOT NULL THEN
          SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
          FROM kasalar WHERE banka_id = v_banka_id AND kasa_tipi = 'banka' AND aktif = true LIMIT 1;
        END IF;
        IF v_kasa_id IS NULL THEN
          SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
          FROM kasalar WHERE kasa_tipi = 'banka' AND aktif = true LIMIT 1;
        END IF;
      ELSIF v_odeme_sekli = 'cek' THEN
        SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
        FROM kasalar WHERE kasa_tipi = 'cek' AND aktif = true LIMIT 1;
      ELSIF v_odeme_sekli = 'senet' THEN
        SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
        FROM kasalar WHERE kasa_tipi = 'senet' AND aktif = true LIMIT 1;
      END IF;
      
      -- Kasa hareketi oluştur
      IF v_kasa_id IS NOT NULL THEN
        INSERT INTO kasa_hareketleri (
          kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
          aciklama, belge_tipi, belge_no, referans_id
        ) VALUES (
          v_kasa_id, 'giris', NEW.toplam_tutar, v_kasa_bakiye, v_kasa_bakiye + NEW.toplam_tutar,
          'Satış - ' || NEW.satis_no || ' (' || v_odeme_sekli || ')', 'satis', NEW.satis_no, NEW.id
        );
        
        -- Kasa bakiyesini güncelle
        UPDATE kasalar 
        SET guncel_bakiye = guncel_bakiye + NEW.toplam_tutar,
            guncelleme_tarihi = NOW()
        WHERE id = v_kasa_id;
      END IF;
    END;
  END IF;
  
  -- Cari hesap işlemleri
  IF NEW.cari_hesap_id IS NOT NULL THEN
    DECLARE
      v_cari_bakiye DECIMAL;
    BEGIN
      SELECT bakiye INTO v_cari_bakiye
      FROM cari_hesaplar WHERE id = NEW.cari_hesap_id;
      
      IF v_odeme_sekli = 'veresiye' THEN
        -- Veresiye satış - borç artır
        INSERT INTO cari_hesap_hareketleri (
          cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
          aciklama, belge_tipi, belge_no, referans_id
        ) VALUES (
          NEW.cari_hesap_id, 'borc', NEW.toplam_tutar, v_cari_bakiye, v_cari_bakiye + NEW.toplam_tutar,
          'Satış - ' || NEW.satis_no, 'satis', NEW.satis_no, NEW.id
        );
        
        UPDATE cari_hesaplar 
        SET bakiye = bakiye + NEW.toplam_tutar,
            guncelleme_tarihi = NOW()
        WHERE id = NEW.cari_hesap_id;
      ELSE
        -- Peşin satış - satış kaydı
        INSERT INTO cari_hesap_hareketleri (
          cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
          aciklama, belge_tipi, belge_no, referans_id
        ) VALUES (
          NEW.cari_hesap_id, 'satis', NEW.toplam_tutar, v_cari_bakiye, v_cari_bakiye,
          'Satış - ' || NEW.satis_no || ' (' || v_odeme_sekli || ')', 'satis', NEW.satis_no, NEW.id
        );
      END IF;
    END;
  END IF;
  
  RETURN NEW;
END;
$$;


--
-- Name: search_product_by_barcode(character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.search_product_by_barcode(p_barkod character varying) RETURNS TABLE(stok_id uuid, stok_kodu character varying, stok_adi character varying, barkod character varying, barkod_tipi character varying, satis_fiyati numeric, eldeki_miktar numeric)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    -- Ana barkodda ara
    SELECT 
        s.id,
        s.stok_kodu,
        s.stok_adi,
        s.barkod,
        'ana'::VARCHAR as barkod_tipi,
        s.satis_fiyati,
        s.eldeki_miktar
    FROM stoklar s
    WHERE s.barkod = p_barkod
    
    UNION
    
    -- Alternatif barkodlarda ara
    SELECT 
        s.id,
        s.stok_kodu,
        s.stok_adi,
        ub.barkod,
        ub.barkod_tipi,
        s.satis_fiyati,
        s.eldeki_miktar
    FROM stoklar s
    INNER JOIN urun_barkodlari ub ON s.id = ub.stok_id
    WHERE ub.barkod = p_barkod AND ub.aktif = true;
END;
$$;


--
-- Name: search_variant_by_barcode(character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.search_variant_by_barcode(p_barkod character varying) RETURNS TABLE(varyant_id uuid, varyant_stok_id uuid, varyant_adi character varying, varyant_barkod character varying, grup_id uuid, grup_adi character varying, ana_stok_id uuid, ana_stok_adi character varying, ana_stok_kodu character varying)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        uv.id as varyant_id,
        uv.stok_id as varyant_stok_id,
        uv.varyant_adi,
        uv.varyant_barkod,
        vg.id as grup_id,
        vg.grup_adi,
        vg.ana_stok_id,
        s.stok_adi as ana_stok_adi,
        s.stok_kodu as ana_stok_kodu
    FROM urun_varyantlari uv
    JOIN varyant_gruplari vg ON uv.varyant_grup_id = vg.id
    JOIN stoklar s ON vg.ana_stok_id = s.id
    WHERE uv.varyant_barkod = p_barkod AND uv.aktif = true;
END;
$$;


