--
-- Name: tahsil_et(uuid, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.tahsil_et(p_hareket_id uuid, p_hedef_kasa_id uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_hareket RECORD;
  v_hedef_kasa RECORD;
  v_kaynak_kasa RECORD;
BEGIN
  -- Hareket bilgilerini al
  SELECT * INTO v_hareket
  FROM kasa_hareketleri
  WHERE id = p_hareket_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Hareket bulunamadı: %', p_hareket_id;
  END IF;
  
  -- Hedef kasa bilgilerini al
  SELECT * INTO v_hedef_kasa
  FROM kasalar
  WHERE id = p_hedef_kasa_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Hedef kasa bulunamadı: %', p_hedef_kasa_id;
  END IF;
  
  -- Kaynak kasa bilgilerini al
  SELECT * INTO v_kaynak_kasa
  FROM kasalar
  WHERE id = v_hareket.kasa_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Kaynak kasa bulunamadı: %', v_hareket.kasa_id;
  END IF;
  
  -- Kaynak kasadan çıkış hareketi oluştur
  INSERT INTO kasa_hareketleri (
    kasa_id,
    hareket_tipi,
    tutar,
    onceki_bakiye,
    sonraki_bakiye,
    aciklama,
    belge_tipi,
    belge_no,
    referans_id,
    islem_tarihi,
    olusturma_tarihi
  ) VALUES (
    v_kaynak_kasa.id,
    'cikis',
    v_hareket.tutar,
    v_kaynak_kasa.guncel_bakiye,
    v_kaynak_kasa.guncel_bakiye - v_hareket.tutar,
    'Tahsilat - ' || v_hareket.aciklama,
    'tahsilat',
    v_hareket.belge_no,
    v_hareket.referans_id,
    NOW(),
    NOW()
  );
  
  -- Hedef kasaya giriş hareketi oluştur
  INSERT INTO kasa_hareketleri (
    kasa_id,
    hareket_tipi,
    tutar,
    onceki_bakiye,
    sonraki_bakiye,
    aciklama,
    belge_tipi,
    belge_no,
    referans_id,
    islem_tarihi,
    olusturma_tarihi
  ) VALUES (
    v_hedef_kasa.id,
    'giris',
    v_hareket.tutar,
    v_hedef_kasa.guncel_bakiye,
    v_hedef_kasa.guncel_bakiye + v_hareket.tutar,
    'Tahsilat - ' || v_hareket.aciklama,
    'tahsilat',
    v_hareket.belge_no,
    v_hareket.referans_id,
    NOW(),
    NOW()
  );
  
  -- Kasaların bakiyelerini güncelle
  UPDATE kasalar
  SET guncel_bakiye = guncel_bakiye - v_hareket.tutar,
      guncelleme_tarihi = NOW()
  WHERE id = v_kaynak_kasa.id;
  
  UPDATE kasalar
  SET guncel_bakiye = guncel_bakiye + v_hareket.tutar,
      guncelleme_tarihi = NOW()
  WHERE id = v_hedef_kasa.id;
  
  -- Orijinal hareketi güncelle (tahsil edildi olarak işaretle)
  UPDATE kasa_hareketleri
  SET aciklama = aciklama || ' [TAHSIL EDILDI]',
      guncelleme_tarihi = NOW()
  WHERE id = p_hareket_id;
  
END;
$$;


--
-- Name: tahsil_et(uuid, uuid, numeric, text, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.tahsil_et(p_kaynak_kasa_id uuid, p_hedef_kasa_id uuid, p_tutar numeric, p_aciklama text, p_referans_hareket_id uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
declare
    kaynak_kasa_bakiye numeric;
    hedef_kasa_bakiye numeric;
begin
    -- Kaynak ve hedef kasaların mevcut bakiyelerini kilitle
    select guncel_bakiye into kaynak_kasa_bakiye from public.kasalar where id = p_kaynak_kasa_id for update;
    select guncel_bakiye into hedef_kasa_bakiye from public.kasalar where id = p_hedef_kasa_id for update;

    -- 1. Kaynak kasadan (portföy) çıkış yap
    insert into public.kasa_hareketleri(kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, aciklama, belge_tipi, referans_id, islem_tarihi, olusturma_tarihi)
    values (
        p_kaynak_kasa_id,
        'cikis',
        p_tutar,
        kaynak_kasa_bakiye,
        kaynak_kasa_bakiye - p_tutar,
        p_aciklama || ' (Portföyden Çıkış)',
        'tahsilat',
        p_referans_hareket_id,
        now(),
        now()
    );

    update public.kasalar
    set guncel_bakiye = kaynak_kasa_bakiye - p_tutar
    where id = p_kaynak_kasa_id;

    -- 2. Hedef kasaya (banka) giriş yap
    insert into public.kasa_hareketleri(kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, aciklama, belge_tipi, referans_id, islem_tarihi, olusturma_tarihi)
    values (
        p_hedef_kasa_id,
        'giris',
        p_tutar,
        hedef_kasa_bakiye,
        hedef_kasa_bakiye + p_tutar,
        p_aciklama || ' (Bankaya Giriş)',
        'tahsilat',
        p_referans_hareket_id,
        now(),
        now()
    );

    update public.kasalar
    set guncel_bakiye = hedef_kasa_bakiye + p_tutar
    where id = p_hedef_kasa_id;

    -- Not: İsteğe bağlı olarak orijinal hareket güncellenebilir.
    -- Örneğin, tahsil edildiğini belirtmek için bir alan eklenebilir.
    -- update public.kasa_hareketleri set durumu = 'tahsil_edildi' where id = p_referans_hareket_id;

end;
$$;


--
-- Name: tahsilat_trigger_function(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.tahsilat_trigger_function() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_tahsilat_tipi TEXT;
  v_banka_id UUID;
  v_manual_approval BOOLEAN;
BEGIN
  -- Manuel onay kontrolü
  v_manual_approval := NEW.aciklama LIKE '%MANUAL_APPROVAL%';
  
  IF v_manual_approval THEN
    RETURN NEW;
  END IF;
  
  v_tahsilat_tipi := NEW.tahsilat_tipi;
  v_banka_id := NEW.banka_id;
  
  -- Cari hesap işlemleri
  IF NEW.cari_hesap_id IS NOT NULL THEN
    DECLARE
      v_cari_bakiye DECIMAL;
    BEGIN
      SELECT bakiye INTO v_cari_bakiye
      FROM cari_hesaplar WHERE id = NEW.cari_hesap_id;
      
      INSERT INTO cari_hesap_hareketleri (
        cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
        aciklama, belge_tipi, belge_no, referans_id
      ) VALUES (
        NEW.cari_hesap_id, 'alacak', NEW.tutar, v_cari_bakiye, v_cari_bakiye - NEW.tutar,
        'Tahsilat - ' || NEW.tahsilat_no || ' (' || v_tahsilat_tipi || ')', 'tahsilat', NEW.tahsilat_no, NEW.id
      );
      
      UPDATE cari_hesaplar 
      SET bakiye = bakiye - NEW.tutar,
          guncelleme_tarihi = NOW()
      WHERE id = NEW.cari_hesap_id;
    END;
  END IF;
  
  -- Kasa işlemleri
  IF v_tahsilat_tipi IN ('nakit', 'kredi_karti', 'banka_transferi', 'cek', 'senet') THEN
    DECLARE
      v_kasa_id UUID;
      v_kasa_bakiye DECIMAL;
    BEGIN
      -- Kasa seçimi
      IF v_tahsilat_tipi = 'nakit' THEN
        SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
        FROM kasalar WHERE kasa_tipi = 'nakit' AND aktif = true LIMIT 1;
      ELSIF v_tahsilat_tipi IN ('kredi_karti', 'banka_transferi') THEN
        IF v_banka_id IS NOT NULL THEN
          SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
          FROM kasalar WHERE banka_id = v_banka_id AND kasa_tipi = 'banka' AND aktif = true LIMIT 1;
        END IF;
        IF v_kasa_id IS NULL THEN
          SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
          FROM kasalar WHERE kasa_tipi = 'banka' AND aktif = true LIMIT 1;
        END IF;
      ELSIF v_tahsilat_tipi = 'cek' THEN
        SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
        FROM kasalar WHERE kasa_tipi = 'cek' AND aktif = true LIMIT 1;
      ELSIF v_tahsilat_tipi = 'senet' THEN
        SELECT id, guncel_bakiye INTO v_kasa_id, v_kasa_bakiye
        FROM kasalar WHERE kasa_tipi = 'senet' AND aktif = true LIMIT 1;
      END IF;
      
      -- Kasa hareketi oluştur
      IF v_kasa_id IS NOT NULL THEN
        INSERT INTO kasa_hareketleri (
          kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
          aciklama, belge_tipi, belge_no, referans_id
        ) VALUES (
          v_kasa_id, 'giris', NEW.tutar, v_kasa_bakiye, v_kasa_bakiye + NEW.tutar,
          'Tahsilat - ' || NEW.tahsilat_no || ' (' || v_tahsilat_tipi || ')', 'tahsilat', NEW.tahsilat_no, NEW.id
        );
        
        -- Kasa bakiyesini güncelle
        UPDATE kasalar 
        SET guncel_bakiye = guncel_bakiye + NEW.tutar,
            guncelleme_tarihi = NOW()
        WHERE id = v_kasa_id;
      END IF;
    END;
  END IF;
  
  RETURN NEW;
END;
$$;


--
-- Name: tespit_adet_uyusmazligi(uuid, jsonb, jsonb, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.tespit_adet_uyusmazligi(p_siparis_durum_id uuid, p_beklenen_veriler jsonb, p_bulunan_veriler jsonb, p_kullanici_id uuid) RETURNS TABLE(uyusmazlik_var boolean, uyusmazlik_sayisi integer, detaylar jsonb)
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_farklar JSONB := '[]'::jsonb;
  v_beklenen JSONB;
  v_bulunan JSONB;
  v_fark_sayisi INTEGER := 0;
BEGIN
  -- Her ürün için karşılaştırma yap
  FOR v_beklenen IN SELECT * FROM jsonb_array_elements(p_beklenen_veriler)
  LOOP
    -- Aynı ürünü bulunan verilerde ara
    SELECT * INTO v_bulunan 
    FROM jsonb_array_elements(p_bulunan_veriler)
    WHERE (value->>'stok_id') = (v_beklenen->>'stok_id')
    LIMIT 1;
    
    -- Adet karşılaştırması
    IF v_bulunan IS NULL OR 
       (v_beklenen->>'miktar')::INTEGER != (v_bulunan->>'miktar')::INTEGER THEN
      
      v_fark_sayisi := v_fark_sayisi + 1;
      
      -- Farkı kaydet
      v_farklar := v_farklar || jsonb_build_object(
        'stok_id', v_beklenen->>'stok_id',
        'urun_adi', v_beklenen->>'urun_adi',
        'beklenen', (v_beklenen->>'miktar')::INTEGER,
        'bulunan', COALESCE((v_bulunan->>'miktar')::INTEGER, 0),
        'fark', COALESCE((v_bulunan->>'miktar')::INTEGER, 0) - (v_beklenen->>'miktar')::INTEGER
      );
    END IF;
  END LOOP;
  
  -- Sonuçları döndür
  RETURN QUERY SELECT 
    v_fark_sayisi > 0,
    v_fark_sayisi,
    v_farklar;
END;
$$;


