--
-- Name: update_alis_faturasi(uuid, jsonb, date, date, text, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.update_alis_faturasi(p_alis_id uuid, p_yeni_kalemler jsonb, p_fatura_tarihi date, p_vade_tarihi date, p_aciklama text, p_cari_hesap_id uuid DEFAULT NULL::uuid) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_eski_alis RECORD;
  v_yeni_ara_toplam NUMERIC := 0;
  v_yeni_kdv_tutari NUMERIC := 0;
  v_yeni_toplam NUMERIC := 0;
  v_kalem JSONB;
  v_kalem_toplam NUMERIC;
  v_kalem_kdv NUMERIC;
  v_eski_cari_id UUID;
  v_yeni_cari_id UUID;
  v_cari_degisti BOOLEAN := FALSE;
  v_stok_record RECORD;
BEGIN
  -- 1. Eski alış bilgilerini al
  SELECT * INTO v_eski_alis FROM alislar WHERE id = p_alis_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Alış faturası bulunamadı: %', p_alis_id;
  END IF;
  
  v_eski_cari_id := v_eski_alis.tedarikci_id;
  v_yeni_cari_id := COALESCE(p_cari_hesap_id, v_eski_cari_id);
  v_cari_degisti := (v_eski_cari_id IS DISTINCT FROM v_yeni_cari_id);
  
  -- 2. Eski stok hareketlerini geri al
  UPDATE stoklar s
  SET eldeki_miktar = s.eldeki_miktar - sh.miktar, guncelleme_tarihi = NOW()
  FROM stok_hareketleri sh
  WHERE sh.stok_id = s.id AND sh.referans_id = p_alis_id AND sh.hareket_tipi = 'giris';
  
  DELETE FROM stok_hareketleri WHERE referans_id = p_alis_id;
  
  -- 3. Cari hesap değiştiyse eski hareketi sil
  IF v_cari_degisti THEN
    DELETE FROM cari_hesap_hareketleri WHERE referans_id = p_alis_id AND belge_tipi = 'alis';
    IF v_eski_cari_id IS NOT NULL THEN
      PERFORM recalculate_cari_bakiye(v_eski_cari_id);
    END IF;
  END IF;
  
  -- 4. Eski kalemleri sil
  DELETE FROM alis_kalemleri WHERE alis_id = p_alis_id;
  
  -- 5. Yeni kalemleri ekle
  FOR v_kalem IN SELECT * FROM jsonb_array_elements(p_yeni_kalemler)
  LOOP
    -- Stok bilgilerini al
    SELECT stok_kodu, stok_adi, barkod INTO v_stok_record
    FROM stoklar WHERE id = (v_kalem->>'stok_id')::UUID;
    
    IF NOT FOUND THEN
      RAISE EXCEPTION 'Stok bulunamadı: %', (v_kalem->>'stok_id')::UUID;
    END IF;
    
    -- Hesaplamalar
    v_kalem_toplam := (v_kalem->>'miktar')::NUMERIC * (v_kalem->>'birim_fiyat')::NUMERIC;
    v_kalem_toplam := v_kalem_toplam * (1 - COALESCE((v_kalem->>'iskonto_orani')::NUMERIC, 0) / 100);
    v_kalem_kdv := v_kalem_toplam * COALESCE((v_kalem->>'kdv_orani')::NUMERIC, 18) / 100;
    v_yeni_ara_toplam := v_yeni_ara_toplam + v_kalem_toplam;
    v_yeni_kdv_tutari := v_yeni_kdv_tutari + v_kalem_kdv;
    
    -- Kalem ekle (TÜM kolonlar)
    INSERT INTO alis_kalemleri (
      alis_id, stok_id, stok_kodu, stok_adi, barkod,
      miktar, birim_fiyat, iskonto_orani, kdv_orani, toplam_tutar
    ) VALUES (
      p_alis_id,
      (v_kalem->>'stok_id')::UUID,
      v_stok_record.stok_kodu,
      v_stok_record.stok_adi,
      v_stok_record.barkod,
      (v_kalem->>'miktar')::NUMERIC,
      (v_kalem->>'birim_fiyat')::NUMERIC,
      COALESCE((v_kalem->>'iskonto_orani')::NUMERIC, 0),
      COALESCE((v_kalem->>'kdv_orani')::NUMERIC, 18),
      v_kalem_toplam + v_kalem_kdv
    );
    
    -- Stok hareketi
    INSERT INTO stok_hareketleri (stok_id, hareket_tipi, miktar, onceki_miktar, sonraki_miktar, referans_id, islem_tarihi)
    SELECT 
      (v_kalem->>'stok_id')::UUID, 'giris', (v_kalem->>'miktar')::NUMERIC,
      COALESCE(s.eldeki_miktar, 0), COALESCE(s.eldeki_miktar, 0) + (v_kalem->>'miktar')::NUMERIC,
      p_alis_id, p_fatura_tarihi
    FROM stoklar s WHERE s.id = (v_kalem->>'stok_id')::UUID;
  END LOOP;
  
  v_yeni_toplam := v_yeni_ara_toplam + v_yeni_kdv_tutari;
  
  -- 6. Stok güncelle
  UPDATE stoklar s
  SET eldeki_miktar = s.eldeki_miktar + sh.miktar, guncelleme_tarihi = NOW()
  FROM stok_hareketleri sh
  WHERE sh.stok_id = s.id AND sh.referans_id = p_alis_id AND sh.hareket_tipi = 'giris';
  
  -- 7. Alış güncelle
  UPDATE alislar SET 
    tedarikci_id = v_yeni_cari_id, fatura_tarihi = p_fatura_tarihi,
    vade_tarihi = p_vade_tarihi, aciklama = p_aciklama,
    ara_toplam = v_yeni_ara_toplam, kdv_tutari = v_yeni_kdv_tutari,
    toplam_tutar = v_yeni_toplam, guncelleme_tarihi = NOW()
  WHERE id = p_alis_id;
  
  -- 8. Cari hareket
  IF v_cari_degisti THEN
    INSERT INTO cari_hesap_hareketleri (cari_hesap_id, belge_tipi, belge_no, referans_id, islem_tarihi, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, aciklama)
    SELECT v_yeni_cari_id, 'alis', COALESCE(v_eski_alis.fatura_no, v_eski_alis.id::text), p_alis_id, p_fatura_tarihi, 'alacak', v_yeni_toplam,
      COALESCE(c.bakiye, 0), COALESCE(c.bakiye, 0) + v_yeni_toplam,
      format('Alış Faturası - %s (Düzenlendi)', COALESCE(v_eski_alis.fatura_no, v_eski_alis.id::text))
    FROM cari_hesaplar c WHERE c.id = v_yeni_cari_id;
  ELSE
    UPDATE cari_hesap_hareketleri SET tutar = v_yeni_toplam, islem_tarihi = p_fatura_tarihi, guncelleme_tarihi = NOW()
    WHERE referans_id = p_alis_id AND belge_tipi = 'alis';
  END IF;
  
  -- 9. Bakiye hesapla
  IF v_cari_degisti THEN
    IF v_eski_cari_id IS NOT NULL THEN PERFORM recalculate_cari_bakiye(v_eski_cari_id); END IF;
    IF v_yeni_cari_id IS NOT NULL THEN PERFORM recalculate_cari_bakiye(v_yeni_cari_id); END IF;
  ELSE
    IF v_yeni_cari_id IS NOT NULL THEN PERFORM recalculate_cari_bakiye(v_yeni_cari_id); END IF;
  END IF;
  
  RETURN jsonb_build_object('success', true, 'message', 'Alış faturası başarıyla güncellendi', 'fatura_no', COALESCE(v_eski_alis.fatura_no, v_eski_alis.id::text));
EXCEPTION
  WHEN OTHERS THEN RAISE EXCEPTION 'Alış faturası güncellenirken hata: %', SQLERRM;
END;
$$;


