-- Name: update_cari_bakiye(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.update_cari_bakiye() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    mevcut_bakiye NUMERIC(15,2);
BEGIN
    -- Sadece 'borc' ve 'alacak' hareket tiplerinde bakiye güncelle
    -- 'satis' hareket tipinde bakiyeye dokunma
    IF NEW.hareket_tipi IN ('borc', 'alacak') THEN
        -- Mevcut bakiyeyi al
        SELECT bakiye INTO mevcut_bakiye FROM public.cari_hesaplar WHERE id = NEW.cari_hesap_id;
        
        -- Yeni bakiyeyi hesapla
        IF NEW.hareket_tipi = 'borc' THEN
            NEW.sonraki_bakiye = mevcut_bakiye + NEW.tutar;
        ELSE -- alacak
            NEW.sonraki_bakiye = mevcut_bakiye - NEW.tutar;
        END IF;
        
        -- Cari hesap bakiyesini güncelle
        UPDATE public.cari_hesaplar 
        SET bakiye = NEW.sonraki_bakiye, guncelleme_tarihi = now()
        WHERE id = NEW.cari_hesap_id;
    ELSE
        -- 'satis' gibi diğer hareket tiplerinde sonraki_bakiye = onceki_bakiye
        SELECT bakiye INTO mevcut_bakiye FROM public.cari_hesaplar WHERE id = NEW.cari_hesap_id;
        NEW.sonraki_bakiye = mevcut_bakiye;
    END IF;
    
    RETURN NEW;
END;
$$;


