--
-- Name: update_iade_faturasi(uuid, jsonb, date, text, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.update_iade_faturasi(p_iade_id uuid, p_yeni_kalemler jsonb, p_iade_tarihi date, p_aciklama text DEFAULT NULL::text, p_cari_hesap_id uuid DEFAULT NULL::uuid) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_eski_iade RECORD;
  v_yeni_ara_toplam NUMERIC := 0;
  v_yeni_kdv_tutari NUMERIC := 0;
  v_yeni_toplam NUMERIC := 0;
  v_kalem JSONB;
  v_kalem_toplam NUMERIC;
  v_kalem_kdv NUMERIC;
  v_eski_cari_id UUID;
  v_yeni_cari_id UUID;
  v_cari_degisti BOOLEAN := FALSE;
  v_sira_no INTEGER := 1;
BEGIN
  -- 1. Eski iade bilgilerini al
  SELECT * INTO v_eski_iade FROM iadeler WHERE id = p_iade_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'İade faturası bulunamadı: %', p_iade_id;
  END IF;
  
  v_eski_cari_id := v_eski_iade.cari_hesap_id;
  v_yeni_cari_id := COALESCE(p_cari_hesap_id, v_eski_cari_id);
  v_cari_degisti := (v_eski_cari_id IS DISTINCT FROM v_yeni_cari_id);
  
  -- 2. Eski stok hareketlerini geri al
  UPDATE stoklar s
  SET eldeki_miktar = s.eldeki_miktar - sh.miktar, guncelleme_tarihi = NOW()
  FROM stok_hareketleri sh
  WHERE sh.stok_id = s.id AND sh.referans_id = p_iade_id AND sh.hareket_tipi = 'giris';
  
  DELETE FROM stok_hareketleri WHERE referans_id = p_iade_id;
  
  -- 3. Cari hesap değiştiyse eski hareketi sil ve bakiyeyi düzelt
  IF v_cari_degisti THEN
    -- Eski hareketi sil (hem 'iade' hem 'iade_faturasi' tiplerini sil)
    DELETE FROM cari_hesap_hareketleri 
    WHERE referans_id = p_iade_id 
      AND belge_tipi IN ('iade', 'iade_faturasi');
    
    -- Eski cari'nin bakiyesini düzelt (iade tutarını geri ekle)
    IF v_eski_cari_id IS NOT NULL THEN
      UPDATE cari_hesaplar
      SET bakiye = bakiye + v_eski_iade.toplam_tutar,
          guncelleme_tarihi = NOW()
      WHERE id = v_eski_cari_id;
      
      -- Bakiyeyi yeniden hesapla
      PERFORM recalculate_cari_bakiye(v_eski_cari_id);
    END IF;
  END IF;
  
  -- 4. Eski kalemleri sil
  DELETE FROM iade_kalemleri WHERE iade_id = p_iade_id;
  
  -- 5. Yeni kalemleri ekle
  FOR v_kalem IN SELECT * FROM jsonb_array_elements(p_yeni_kalemler)
  LOOP
    -- Stok var mı kontrol et
    IF NOT EXISTS (SELECT 1 FROM stoklar WHERE id = (v_kalem->>'stok_id')::UUID) THEN
      RAISE EXCEPTION 'Stok bulunamadı: %', (v_kalem->>'stok_id')::UUID;
    END IF;
    
    -- Hesaplamalar
    v_kalem_toplam := (v_kalem->>'miktar')::NUMERIC * (v_kalem->>'birim_fiyat')::NUMERIC;
    v_kalem_kdv := v_kalem_toplam * COALESCE((v_kalem->>'kdv_orani')::NUMERIC, 20) / 100;
    v_yeni_ara_toplam := v_yeni_ara_toplam + v_kalem_toplam;
    v_yeni_kdv_tutari := v_yeni_kdv_tutari + v_kalem_kdv;
    
    -- Kalem ekle - TÜM kolonları dahil et (stok_kodu'nu stoklar tablosundan çek)
    INSERT INTO iade_kalemleri (
      iade_id, 
      stok_id,
      stok_kodu,
      satis_kalemi_id,
      miktar, 
      birim_fiyat, 
      kdv_orani, 
      toplam_tutar,
      sira_no
    )
    SELECT
      p_iade_id,
      (v_kalem->>'stok_id')::UUID,
      s.stok_kodu,
      NULL,
      (v_kalem->>'miktar')::NUMERIC,
      (v_kalem->>'birim_fiyat')::NUMERIC,
      COALESCE((v_kalem->>'kdv_orani')::NUMERIC, 20),
      v_kalem_toplam + v_kalem_kdv,
      v_sira_no
    FROM stoklar s
    WHERE s.id = (v_kalem->>'stok_id')::UUID;
    
    v_sira_no := v_sira_no + 1;
    
    -- Stok hareketi
    INSERT INTO stok_hareketleri (
      stok_id, 
      hareket_tipi, 
      miktar, 
      onceki_miktar, 
      sonraki_miktar, 
      referans_id, 
      islem_tarihi
    )
    SELECT 
      (v_kalem->>'stok_id')::UUID, 
      'giris', 
      (v_kalem->>'miktar')::NUMERIC,
      COALESCE(s.eldeki_miktar, 0), 
      COALESCE(s.eldeki_miktar, 0) + (v_kalem->>'miktar')::NUMERIC,
      p_iade_id, 
      p_iade_tarihi
    FROM stoklar s WHERE s.id = (v_kalem->>'stok_id')::UUID;
  END LOOP;
  
  v_yeni_toplam := v_yeni_ara_toplam + v_yeni_kdv_tutari;
  
  -- 6. Stok güncelle
  UPDATE stoklar s
  SET eldeki_miktar = s.eldeki_miktar + sh.miktar, guncelleme_tarihi = NOW()
  FROM stok_hareketleri sh
  WHERE sh.stok_id = s.id AND sh.referans_id = p_iade_id AND sh.hareket_tipi = 'giris';
  
  -- 7. İade güncelle
  UPDATE iadeler SET 
    cari_hesap_id = v_yeni_cari_id, 
    iade_tarihi = p_iade_tarihi, 
    notlar = p_aciklama,
    ara_toplam = v_yeni_ara_toplam, 
    kdv_tutari = v_yeni_kdv_tutari,
    toplam_tutar = v_yeni_toplam, 
    guncelleme_tarihi = NOW()
  WHERE id = p_iade_id;
  
  -- 8. Cari hareket
  IF v_cari_degisti THEN
    INSERT INTO cari_hesap_hareketleri (
      cari_hesap_id, 
      belge_tipi, 
      belge_no, 
      referans_id, 
      islem_tarihi, 
      hareket_tipi, 
      tutar, 
      onceki_bakiye, 
      sonraki_bakiye, 
      aciklama
    )
    SELECT 
      v_yeni_cari_id, 
      'iade', 
      COALESCE(v_eski_iade.iade_no, v_eski_iade.id::text), 
      p_iade_id, 
      p_iade_tarihi, 
      'alacak', 
      -v_yeni_toplam,
      COALESCE(c.bakiye, 0), 
      COALESCE(c.bakiye, 0) - v_yeni_toplam,
      format('İade Faturası - %s (Düzenlendi)', COALESCE(v_eski_iade.iade_no, v_eski_iade.id::text))
    FROM cari_hesaplar c WHERE c.id = v_yeni_cari_id;
  ELSE
    UPDATE cari_hesap_hareketleri 
    SET 
      tutar = -v_yeni_toplam, 
      islem_tarihi = p_iade_tarihi, 
      sonraki_bakiye = onceki_bakiye - v_yeni_toplam, 
      guncelleme_tarihi = NOW()
    WHERE referans_id = p_iade_id AND belge_tipi IN ('iade', 'iade_faturasi');
  END IF;
  
  -- 9. Bakiye hesapla
  IF v_cari_degisti THEN
    IF v_eski_cari_id IS NOT NULL THEN 
      PERFORM recalculate_cari_bakiye(v_eski_cari_id); 
    END IF;
    IF v_yeni_cari_id IS NOT NULL THEN 
      PERFORM recalculate_cari_bakiye(v_yeni_cari_id); 
    END IF;
  ELSE
    IF v_yeni_cari_id IS NOT NULL THEN 
      PERFORM recalculate_cari_bakiye(v_yeni_cari_id); 
    END IF;
  END IF;
  
  RETURN jsonb_build_object(
    'success', true, 
    'message', 'İade faturası başarıyla güncellendi', 
    'fatura_no', COALESCE(v_eski_iade.iade_no, v_eski_iade.id::text)
  );
EXCEPTION
  WHEN OTHERS THEN 
    RAISE EXCEPTION 'İade faturası güncellenirken hata: %', SQLERRM;
END;
$$;


