    -- Mevcut bakiyeyi al
    SELECT guncel_bakiye INTO mevcut_bakiye FROM public.kasalar WHERE id = NEW.kasa_id;
    
    -- Yeni bakiyeyi hesapla
    IF NEW.hareket_tipi = 'giris' THEN
        NEW.sonraki_bakiye = mevcut_bakiye + NEW.tutar;
    ELSE -- cikis
        NEW.sonraki_bakiye = mevcut_bakiye - NEW.tutar;
    END IF;
    
    NEW.onceki_bakiye = mevcut_bakiye;
    
    -- Kasa bakiyesini güncelle
    UPDATE public.kasalar 
    SET guncel_bakiye = NEW.sonraki_bakiye, guncelleme_tarihi = now()
    WHERE id = NEW.kasa_id;
    
    RETURN NEW;
END;
$$;


