    LANGUAGE plpgsql
    AS $$
DECLARE
  v_eski_satis RECORD;
  v_yeni_ara_toplam NUMERIC := 0;
  v_yeni_kdv_tutari NUMERIC := 0;
  v_yeni_toplam NUMERIC := 0;
  v_kalem JSONB;
  v_kalem_toplam NUMERIC;
  v_kalem_kdv NUMERIC;
  v_eski_cari_id UUID;
  v_yeni_cari_id UUID;
  v_cari_degisti BOOLEAN := FALSE;
BEGIN
  SELECT * INTO v_eski_satis FROM satislar WHERE id = p_satis_id;
  IF NOT FOUND THEN RAISE EXCEPTION 'Satış faturası bulunamadı: %', p_satis_id; END IF;
  
  v_eski_cari_id := v_eski_satis.cari_hesap_id;
  v_yeni_cari_id := COALESCE(p_cari_hesap_id, v_eski_cari_id);
  v_cari_degisti := (v_eski_cari_id IS DISTINCT FROM v_yeni_cari_id);
  
  -- Eski stok hareketlerini geri al
  UPDATE stoklar s SET eldeki_miktar = s.eldeki_miktar + sh.miktar, guncelleme_tarihi = NOW()
  FROM stok_hareketleri sh WHERE sh.stok_id = s.id AND sh.referans_id = p_satis_id AND sh.hareket_tipi = 'cikis';
  
  DELETE FROM stok_hareketleri WHERE referans_id = p_satis_id;
  IF v_cari_degisti THEN DELETE FROM cari_hesap_hareketleri WHERE referans_id = p_satis_id AND belge_tipi = 'satis'; END IF;
  DELETE FROM satis_kalemleri WHERE satis_id = p_satis_id;
  
  -- Yeni kalemler
  FOR v_kalem IN SELECT * FROM jsonb_array_elements(p_yeni_kalemler) LOOP
    v_kalem_toplam := (v_kalem->>'miktar')::NUMERIC * (v_kalem->>'birim_fiyat')::NUMERIC;
    v_kalem_toplam := v_kalem_toplam * (1 - COALESCE((v_kalem->>'iskonto_orani')::NUMERIC, 0) / 100);
    v_kalem_kdv := v_kalem_toplam * COALESCE((v_kalem->>'kdv_orani')::NUMERIC, 20) / 100;
    v_yeni_ara_toplam := v_yeni_ara_toplam + v_kalem_toplam;
    v_yeni_kdv_tutari := v_yeni_kdv_tutari + v_kalem_kdv;
    
    INSERT INTO satis_kalemleri (satis_id, stok_id, miktar, birim_fiyat, toplam_tutar, indirim_orani, kdv_orani)
    VALUES (p_satis_id, (v_kalem->>'stok_id')::UUID, (v_kalem->>'miktar')::NUMERIC, (v_kalem->>'birim_fiyat')::NUMERIC,
            v_kalem_toplam, COALESCE((v_kalem->>'iskonto_orani')::NUMERIC, 0), COALESCE((v_kalem->>'kdv_orani')::NUMERIC, 20));
    
    INSERT INTO stok_hareketleri (stok_id, hareket_tipi, miktar, onceki_miktar, sonraki_miktar, referans_id, islem_tarihi)
    SELECT (v_kalem->>'stok_id')::UUID, 'cikis', (v_kalem->>'miktar')::NUMERIC,
           COALESCE(s.eldeki_miktar, 0), COALESCE(s.eldeki_miktar, 0) - (v_kalem->>'miktar')::NUMERIC,
           p_satis_id, p_satis_tarihi
    FROM stoklar s WHERE s.id = (v_kalem->>'stok_id')::UUID;
  END LOOP;
  
  v_yeni_toplam := v_yeni_ara_toplam + v_yeni_kdv_tutari - p_indirim_tutari;
  
  -- STOK KONTROLÜ KALDIRILDI - Negatif stok izin verildi
  -- Stok güncelle (negatife düşebilir)
  UPDATE stoklar s SET eldeki_miktar = s.eldeki_miktar - sh.miktar, guncelleme_tarihi = NOW()
  FROM stok_hareketleri sh WHERE sh.stok_id = s.id AND sh.referans_id = p_satis_id AND sh.hareket_tipi = 'cikis';
  
  UPDATE satislar SET cari_hesap_id = v_yeni_cari_id, satis_tarihi = p_satis_tarihi, odeme_sekli = p_odeme_sekli,
                      notlar = p_notlar, indirim_tutari = p_indirim_tutari, ara_toplam = v_yeni_ara_toplam,
                      kdv_tutari = v_yeni_kdv_tutari, toplam_tutar = v_yeni_toplam, guncelleme_tarihi = NOW()
  WHERE id = p_satis_id;
  
  IF v_cari_degisti THEN
    INSERT INTO cari_hesap_hareketleri (cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, belge_tipi, belge_no, referans_id, aciklama, islem_tarihi)
    SELECT v_yeni_cari_id, CASE WHEN p_odeme_sekli = 'veresiye' THEN 'borc' ELSE 'satis' END, v_yeni_toplam,
           COALESCE(c.bakiye, 0), CASE WHEN p_odeme_sekli = 'veresiye' THEN COALESCE(c.bakiye, 0) + v_yeni_toplam ELSE COALESCE(c.bakiye, 0) END,
           'satis', v_eski_satis.satis_no, p_satis_id, format('Satış Faturası - %s (Düzenlendi)', v_eski_satis.satis_no), p_satis_tarihi
    FROM cari_hesaplar c WHERE c.id = v_yeni_cari_id;
  ELSE
    UPDATE cari_hesap_hareketleri ch SET tutar = v_yeni_toplam,
           hareket_tipi = CASE WHEN p_odeme_sekli = 'veresiye' THEN 'borc' ELSE 'satis' END,
           sonraki_bakiye = CASE WHEN p_odeme_sekli = 'veresiye' THEN ch.onceki_bakiye + v_yeni_toplam ELSE ch.onceki_bakiye END,
           islem_tarihi = p_satis_tarihi, guncelleme_tarihi = NOW()
    WHERE ch.referans_id = p_satis_id AND ch.belge_tipi = 'satis';
  END IF;
  
  UPDATE kasa_hareketleri SET islem_tarihi = p_satis_tarihi WHERE referans_id = p_satis_id AND belge_tipi = 'satis';
  
  IF v_cari_degisti THEN
    PERFORM recalculate_cari_bakiye(v_eski_cari_id);
    PERFORM recalculate_cari_bakiye(v_yeni_cari_id);
  ELSE
    PERFORM recalculate_cari_bakiye(v_yeni_cari_id);
  END IF;
  
  RETURN jsonb_build_object('success', true, 'message', 'Satış faturası başarıyla güncellendi',
                            'eski_toplam', v_eski_satis.toplam_tutar, 'yeni_toplam', v_yeni_toplam,
                            'fark', v_yeni_toplam - v_eski_satis.toplam_tutar, 'cari_degisti', v_cari_degisti);
EXCEPTION WHEN OTHERS THEN RAISE EXCEPTION 'Satış faturası güncellenirken hata: %', SQLERRM;
END;
$$;


