CREATE FUNCTION public.update_stok_durumu() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF NEW.eldeki_miktar = 0 THEN
        NEW.stok_durumu = 'bitti';
    ELSIF NEW.eldeki_miktar <= NEW.minimum_stok THEN
        NEW.stok_durumu = 'kritik';
    ELSE
        NEW.stok_durumu = 'normal';
    END IF;
    
    NEW.guncelleme_tarihi = now();
    RETURN NEW;
END;
$$;


--
-- Name: update_tahsilat(uuid, uuid, numeric, date, text, uuid, text, text, date, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.update_tahsilat(p_tahsilat_id uuid, p_cari_hesap_id uuid, p_tutar numeric, p_tahsilat_tarihi date, p_tahsilat_tipi text, p_banka_id uuid DEFAULT NULL::uuid, p_dekont_no text DEFAULT NULL::text, p_cek_no text DEFAULT NULL::text, p_cek_vade_tarihi date DEFAULT NULL::date, p_aciklama text DEFAULT NULL::text) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_eski_tahsilat RECORD;
  v_eski_cari_id UUID;
  v_yeni_cari_id UUID;
  v_cari_degisti BOOLEAN := FALSE;
BEGIN
  SELECT * INTO v_eski_tahsilat FROM tahsilatlar WHERE id = p_tahsilat_id;
  
  IF NOT FOUND THEN
    RAISE EXCEPTION 'Tahsilat bulunamadı: %', p_tahsilat_id;
  END IF;
  
  v_eski_cari_id := v_eski_tahsilat.cari_hesap_id;
  v_yeni_cari_id := p_cari_hesap_id;
  v_cari_degisti := (v_eski_cari_id IS DISTINCT FROM v_yeni_cari_id);
  
  IF v_cari_degisti THEN
    DELETE FROM cari_hesap_hareketleri WHERE referans_id = p_tahsilat_id AND belge_tipi = 'tahsilat';
    IF v_eski_cari_id IS NOT NULL THEN
      PERFORM recalculate_cari_bakiye(v_eski_cari_id);
    END IF;
  END IF;
  
  UPDATE tahsilatlar SET 
    cari_hesap_id = v_yeni_cari_id, tutar = p_tutar, tahsilat_tarihi = p_tahsilat_tarihi,
    tahsilat_tipi = p_tahsilat_tipi, banka_id = p_banka_id, dekont_no = p_dekont_no,
    cek_no = p_cek_no, cek_vade_tarihi = p_cek_vade_tarihi, aciklama = p_aciklama,
    guncelleme_tarihi = NOW()
  WHERE id = p_tahsilat_id;
  
  IF v_cari_degisti THEN
    INSERT INTO cari_hesap_hareketleri (
      cari_hesap_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye,
      belge_tipi, belge_no, referans_id, aciklama, islem_tarihi
    )
    SELECT 
      v_yeni_cari_id, 'alacak', p_tutar, COALESCE(c.bakiye, 0), COALESCE(c.bakiye, 0) - p_tutar,
      'tahsilat', v_eski_tahsilat.tahsilat_no, p_tahsilat_id,
      format('Tahsilat - %s (Düzenlendi)', v_eski_tahsilat.tahsilat_no), p_tahsilat_tarihi
    FROM cari_hesaplar c WHERE c.id = v_yeni_cari_id;
  ELSE
    UPDATE cari_hesap_hareketleri ch SET 
      tutar = p_tutar, sonraki_bakiye = ch.onceki_bakiye - p_tutar,
      islem_tarihi = p_tahsilat_tarihi, guncelleme_tarihi = NOW()
    WHERE ch.referans_id = p_tahsilat_id AND ch.belge_tipi = 'tahsilat';
  END IF;
  
  -- Kasa hareketlerinin tarihini güncelle
  UPDATE kasa_hareketleri SET 
    islem_tarihi = p_tahsilat_tarihi
  WHERE referans_id = p_tahsilat_id AND belge_tipi = 'tahsilat';
  
  IF v_cari_degisti THEN
    IF v_eski_cari_id IS NOT NULL THEN PERFORM recalculate_cari_bakiye(v_eski_cari_id); END IF;
    IF v_yeni_cari_id IS NOT NULL THEN PERFORM recalculate_cari_bakiye(v_yeni_cari_id); END IF;
  ELSE
    IF v_yeni_cari_id IS NOT NULL THEN PERFORM recalculate_cari_bakiye(v_yeni_cari_id); END IF;
  END IF;
  
  RETURN jsonb_build_object(
    'success', true, 'message', 'Tahsilat başarıyla güncellendi',
    'tahsilat_no', v_eski_tahsilat.tahsilat_no, 'eski_tutar', v_eski_tahsilat.tutar,
    'yeni_tutar', p_tutar, 'fark', p_tutar - v_eski_tahsilat.tutar, 'cari_degisti', v_cari_degisti
  );
EXCEPTION
  WHEN OTHERS THEN RAISE EXCEPTION 'Tahsilat güncellenirken hata: %', SQLERRM;
END;
$$;


