--
-- Name: v_siparis_akis_ozeti; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.v_siparis_akis_ozeti AS
 SELECT sd.id,
    sd.siparis_id,
    sd.siparis_numarasi,
    sd.musteri_adi,
    sd.durum,
    sd.onay_durumu,
    sd.adet_uyusmazligi,
    sd.duzenleme_sayisi,
    sd.yeniden_kontrol_sayisi,
    sd.kontrol_referans_tipi,
    u_hazirla.email AS hazirlayan_email,
    u_kontrol.email AS kontrol_eden_email,
    u_onay.email AS onaylayan_email,
    u_yukleme.email AS yukleyen_email,
    sd.hazirlama_baslama_tarihi,
    sd.hazirlama_bitis_tarihi,
    sd.kontrol_tarihi,
    sd.onay_tarihi,
    sd.yukleme_tarihi,
    (EXTRACT(epoch FROM (sd.hazirlama_bitis_tarihi - sd.hazirlama_baslama_tarihi)) / (60)::numeric) AS hazirlama_suresi_dk,
    (EXTRACT(epoch FROM (sd.kontrol_tarihi - sd.hazirlama_bitis_tarihi)) / (60)::numeric) AS kontrol_suresi_dk,
    (EXTRACT(epoch FROM (sd.yukleme_tarihi - sd.kontrol_tarihi)) / (60)::numeric) AS yukleme_suresi_dk,
    sd.created_at,
    sd.updated_at
   FROM ((((public.siparis_durumlari sd
     LEFT JOIN auth.users u_hazirla ON ((sd.hazirlayan_kullanici_id = u_hazirla.id)))
     LEFT JOIN auth.users u_kontrol ON ((sd.kontrol_kullanici_id = u_kontrol.id)))
     LEFT JOIN auth.users u_onay ON ((sd.onaylayan_kullanici_id = u_onay.id)))
     LEFT JOIN auth.users u_yukleme ON ((sd.yukleme_kullanici_id = u_yukleme.id)));


