20240720131441	{"create or replace function public.tahsil_et(\r\n    p_kaynak_kasa_id uuid,\r\n    p_hedef_kasa_id uuid,\r\n    p_tutar numeric,\r\n    p_aciklama text,\r\n    p_referans_hareket_id uuid\r\n)\r\nreturns void as $$\r\ndeclare\r\n    kaynak_kasa_bakiye numeric;\r\n    hedef_kasa_bakiye numeric;\r\nbegin\r\n    -- Kaynak ve hedef kasaların mevcut bakiyelerini kilitle\r\n    select guncel_bakiye into kaynak_kasa_bakiye from public.kasalar where id = p_kaynak_kasa_id for update;\r\n    select guncel_bakiye into hedef_kasa_bakiye from public.kasalar where id = p_hedef_kasa_id for update;\r\n\r\n    -- 1. Kaynak kasadan (portföy) çıkış yap\r\n    insert into public.kasa_hareketleri(kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, aciklama, belge_tipi, referans_id, islem_tarihi, olusturma_tarihi)\r\n    values (\r\n        p_kaynak_kasa_id,\r\n        'cikis',\r\n        p_tutar,\r\n        kaynak_kasa_bakiye,\r\n        kaynak_kasa_bakiye - p_tutar,\r\n        p_aciklama || ' (Portföyden Çıkış)',\r\n        'tahsilat',\r\n        p_referans_hareket_id,\r\n        now(),\r\n        now()\r\n    );\r\n\r\n    update public.kasalar\r\n    set guncel_bakiye = kaynak_kasa_bakiye - p_tutar\r\n    where id = p_kaynak_kasa_id;\r\n\r\n    -- 2. Hedef kasaya (banka) giriş yap\r\n    insert into public.kasa_hareketleri(kasa_id, hareket_tipi, tutar, onceki_bakiye, sonraki_bakiye, aciklama, belge_tipi, referans_id, islem_tarihi, olusturma_tarihi)\r\n    values (\r\n        p_hedef_kasa_id,\r\n        'giris',\r\n        p_tutar,\r\n        hedef_kasa_bakiye,\r\n        hedef_kasa_bakiye + p_tutar,\r\n        p_aciklama || ' (Bankaya Giriş)',\r\n        'tahsilat',\r\n        p_referans_hareket_id,\r\n        now(),\r\n        now()\r\n    );\r\n\r\n    update public.kasalar\r\n    set guncel_bakiye = hedef_kasa_bakiye + p_tutar\r\n    where id = p_hedef_kasa_id;\r\n\r\n    -- Not: İsteğe bağlı olarak orijinal hareket güncellenebilir.\r\n    -- Örneğin, tahsil edildiğini belirtmek için bir alan eklenebilir.\r\n    -- update public.kasa_hareketleri set durumu = 'tahsil_edildi' where id = p_referans_hareket_id;\r\n\r\nend;\r\n$$ language plpgsql"}	create_tahsil_et_function	\N	\N
20250101000000	{"-- Stok düşürme sorununu düzelt\r\n-- Mevcut trigger'ları kontrol et ve düzelt\r\n\r\n-- Önce mevcut trigger'ları kaldır\r\nDROP TRIGGER IF EXISTS trigger_complete_sale_process ON public.satislar","DROP TRIGGER IF EXISTS trigger_sale_account_movement ON public.satislar","DROP TRIGGER IF EXISTS trigger_sale_stock_movement ON public.satis_kalemleri","-- Function'ı yeniden oluştur\r\nCREATE OR REPLACE FUNCTION public.create_sale_with_movements()\r\nRETURNS TRIGGER AS $$\r\nDECLARE\r\n    item_record RECORD;\r\n    current_balance NUMERIC(15,2) := 0;\r\nBEGIN\r\n    -- Debug için log ekle\r\n    RAISE NOTICE 'Trigger çalıştı: satis_id=%, durum=%, eski_durum=%', NEW.id, NEW.satis_durumu, COALESCE(OLD.satis_durumu, 'null');\r\n    \r\n    -- Satış tamamlandığında (INSERT veya UPDATE ile tamamlandi durumuna geçtiğinde)\r\n    IF NEW.satis_durumu = 'tamamlandi' AND (TG_OP = 'INSERT' OR (OLD IS NULL OR OLD.satis_durumu IS NULL OR OLD.satis_durumu != 'tamamlandi')) THEN\r\n        \r\n        RAISE NOTICE 'Satış işlemi başlatılıyor: %', NEW.satis_no;\r\n        \r\n        -- Satış kalemlerini işle\r\n        FOR item_record IN \r\n            SELECT sk.*, s.stok_adi, s.eldeki_miktar \r\n            FROM public.satis_kalemleri sk\r\n            JOIN public.stoklar s ON sk.stok_id = s.id\r\n            WHERE sk.satis_id = NEW.id\r\n        LOOP\r\n            RAISE NOTICE 'Stok işleniyor: stok_id=%, miktar=%, mevcut_stok=%', item_record.stok_id, item_record.miktar, item_record.eldeki_miktar;\r\n            \r\n            -- Stok yeterli mi kontrol et\r\n            IF item_record.eldeki_miktar < item_record.miktar THEN\r\n                RAISE EXCEPTION 'Yetersiz stok: % için %d adet istendi, %d adet mevcut', item_record.stok_adi, item_record.miktar, item_record.eldeki_miktar;\r\n            END IF;\r\n            \r\n            -- Stok hareketi oluştur\r\n            INSERT INTO public.stok_hareketleri (\r\n                stok_id,\r\n                hareket_tipi,\r\n                miktar,\r\n                onceki_miktar,\r\n                sonraki_miktar,\r\n                birim_fiyat,\r\n                toplam_tutar,\r\n                aciklama,\r\n                belge_tipi,\r\n                belge_no,\r\n                referans_id\r\n            ) VALUES (\r\n                item_record.stok_id,\r\n                'cikis',\r\n                item_record.miktar,\r\n                item_record.eldeki_miktar,\r\n                item_record.eldeki_miktar - item_record.miktar,\r\n                item_record.birim_fiyat,\r\n                item_record.toplam_tutar,\r\n                'Satış - ' || NEW.satis_no || ' - ' || item_record.stok_adi,\r\n                'satis',\r\n                NEW.satis_no,\r\n                NEW.id\r\n            );\r\n            \r\n            -- Stok miktarını güncelle\r\n            UPDATE public.stoklar \r\n            SET eldeki_miktar = eldeki_miktar - item_record.miktar,\r\n                guncelleme_tarihi = now()\r\n            WHERE id = item_record.stok_id;\r\n            \r\n            RAISE NOTICE 'Stok güncellendi: stok_id=%, yeni_miktar=%', item_record.stok_id, item_record.eldeki_miktar - item_record.miktar;\r\n        END LOOP;\r\n        \r\n        -- Cari hesap hareketi oluştur (eğer cari hesap varsa)\r\n        IF NEW.cari_hesap_id IS NOT NULL THEN\r\n            -- Mevcut bakiyeyi al\r\n            SELECT COALESCE(bakiye, 0) INTO current_balance \r\n            FROM public.cari_hesaplar \r\n            WHERE id = NEW.cari_hesap_id;\r\n            \r\n            RAISE NOTICE 'Cari hesap hareketi oluşturuluyor: cari_id=%, tutar=%, mevcut_bakiye=%', NEW.cari_hesap_id, NEW.toplam_tutar, current_balance;\r\n            \r\n            -- Sadece veresiye satışlarda bakiyeye ekle\r\n            IF NEW.odeme_sekli = 'veresiye' THEN\r\n                -- Cari hesap hareketini oluştur\r\n                INSERT INTO public.cari_hesap_hareketleri (\r\n                    cari_hesap_id,\r\n                    hareket_tipi,\r\n                    tutar,\r\n                    onceki_bakiye,\r\n                    sonraki_bakiye,\r\n                    aciklama,\r\n                    belge_tipi,\r\n                    belge_no,\r\n                    referans_id,\r\n                    islem_tarihi\r\n                ) VALUES (\r\n                    NEW.cari_hesap_id,\r\n                    'borc',\r\n                    NEW.toplam_tutar,\r\n                    current_balance,\r\n                    current_balance + NEW.toplam_tutar,\r\n                    'Satış Faturası - ' || NEW.satis_no,\r\n                    'satis_faturasi',\r\n                    NEW.satis_no,\r\n                    NEW.id,\r\n                    NOW()\r\n                );\r\n                \r\n                -- Cari hesap bakiyesini güncelle\r\n                UPDATE public.cari_hesaplar \r\n                SET bakiye = current_balance + NEW.toplam_tutar,\r\n                    guncelleme_tarihi = now()\r\n                WHERE id = NEW.cari_hesap_id;\r\n            ELSE\r\n                -- Diğer ödeme şekillerinde sadece hareket kaydı ekle, bakiyeyi değiştirme\r\n                INSERT INTO public.cari_hesap_hareketleri (\r\n                    cari_hesap_id,\r\n                    hareket_tipi,\r\n                    tutar,\r\n                    onceki_bakiye,\r\n                    sonraki_bakiye,\r\n                    aciklama,\r\n                    belge_tipi,\r\n                    belge_no,\r\n                    referans_id,\r\n                    islem_tarihi\r\n                ) VALUES (\r\n                    NEW.cari_hesap_id,\r\n                    'satis',\r\n                    NEW.toplam_tutar,\r\n                    current_balance,\r\n                    current_balance,\r\n                    'Satış Faturası - ' || NEW.satis_no || ' (' || NEW.odeme_sekli || ')',\r\n                    'satis_faturasi',\r\n                    NEW.satis_no,\r\n                    NEW.id,\r\n                    NOW()\r\n                );\r\n            END IF;\r\n        END IF;\r\n        \r\n        RAISE NOTICE 'Satış işlemi tamamlandı: %', NEW.satis_no;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$$ LANGUAGE plpgsql","-- Trigger'ı yeniden oluştur\r\nCREATE TRIGGER trigger_complete_sale_process\r\n    AFTER INSERT OR UPDATE ON public.satislar\r\n    FOR EACH ROW\r\n    EXECUTE FUNCTION public.create_sale_with_movements()","-- Satış numarası otomatik oluşturma function'ını da güncelleyelim\r\nCREATE OR REPLACE FUNCTION public.generate_satis_no()\r\nRETURNS TRIGGER AS $$\r\nDECLARE\r\n    next_number INTEGER;\r\n    formatted_number TEXT;\r\nBEGIN\r\n    IF NEW.satis_no IS NULL OR NEW.satis_no = '' THEN\r\n        -- Bugünkü satış sayısını al\r\n        SELECT COALESCE(COUNT(*), 0) + 1 INTO next_number\r\n        FROM public.satislar \r\n        WHERE DATE(satis_tarihi) = DATE(NEW.satis_tarihi);\r\n        \r\n        -- Format: SAT-YYYYMMDD-XXX\r\n        formatted_number := 'SAT-' || TO_CHAR(NEW.satis_tarihi, 'YYYYMMDD') || '-' || LPAD(next_number::TEXT, 3, '0');\r\n        NEW.satis_no := formatted_number;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$$ LANGUAGE plpgsql","-- Satış numarası trigger'ı\r\nDROP TRIGGER IF EXISTS trigger_generate_satis_no ON public.satislar","CREATE TRIGGER trigger_generate_satis_no\r\n    BEFORE INSERT ON public.satislar\r\n    FOR EACH ROW\r\n    EXECUTE FUNCTION public.generate_satis_no()","-- İade işlemi tamamlandığında otomatik stok artırma function'ı\r\nCREATE OR REPLACE FUNCTION public.create_iade_movements()\r\nRETURNS TRIGGER AS $$\r\nDECLARE\r\n    item_record RECORD;\r\n    current_balance NUMERIC(15,2) := 0;\r\nBEGIN\r\n    -- Debug için log ekle\r\n    RAISE NOTICE 'İade trigger çalıştı: iade_id=%, durum=%, eski_durum=%', NEW.id, NEW.iade_durumu, COALESCE(OLD.iade_durumu, 'null');\r\n    \r\n    -- İade tamamlandığında (INSERT veya UPDATE ile tamamlandi durumuna geçtiğinde)\r\n    IF NEW.iade_durumu = 'tamamlandi' AND (TG_OP = 'INSERT' OR (OLD IS NULL OR OLD.iade_durumu IS NULL OR OLD.iade_durumu != 'tamamlandi')) THEN\r\n        \r\n        RAISE NOTICE 'İade işlemi başlatılıyor: %', NEW.iade_no;\r\n        \r\n        -- İade kalemlerini işle\r\n        FOR item_record IN \r\n            SELECT ik.*, s.stok_adi, s.eldeki_miktar \r\n            FROM public.iade_kalemleri ik\r\n            JOIN public.stoklar s ON ik.stok_id = s.id\r\n            WHERE ik.iade_id = NEW.id\r\n        LOOP\r\n            RAISE NOTICE 'İade stok işleniyor: stok_id=%, miktar=%, mevcut_stok=%', item_record.stok_id, item_record.miktar, item_record.eldeki_miktar;\r\n            \r\n            -- Stok hareketi oluştur (stok artışı)\r\n            INSERT INTO public.stok_hareketleri (\r\n                stok_id,\r\n                hareket_tipi,\r\n                miktar,\r\n                onceki_miktar,\r\n                sonraki_miktar,\r\n                birim_fiyat,\r\n                toplam_tutar,\r\n                aciklama,\r\n                belge_tipi,\r\n                belge_no,\r\n                referans_id\r\n            ) VALUES (\r\n                item_record.stok_id,\r\n                'giris',\r\n                item_record.miktar,\r\n                item_record.eldeki_miktar,\r\n                item_record.eldeki_miktar + item_record.miktar,\r\n                item_record.birim_fiyat,\r\n                item_record.toplam_tutar,\r\n                'İade - ' || NEW.iade_no || ' - ' || item_record.stok_adi,\r\n                'iade',\r\n                NEW.iade_no,\r\n                NEW.id\r\n            );\r\n            \r\n            -- Stok miktarını artır\r\n            UPDATE public.stoklar \r\n            SET eldeki_miktar = eldeki_miktar + item_record.miktar,\r\n                guncelleme_tarihi = now()\r\n            WHERE id = item_record.stok_id;\r\n            \r\n            RAISE NOTICE 'İade stok güncellendi: stok_id=%, yeni_miktar=%', item_record.stok_id, item_record.eldeki_miktar + item_record.miktar;\r\n        END LOOP;\r\n        \r\n        -- Cari hesap hareketi oluştur (eğer cari hesap varsa)\r\n        IF NEW.cari_hesap_id IS NOT NULL THEN\r\n            -- Mevcut bakiyeyi al\r\n            SELECT COALESCE(bakiye, 0) INTO current_balance \r\n            FROM public.cari_hesaplar \r\n            WHERE id = NEW.cari_hesap_id;\r\n            \r\n            -- Cari hesap hareketini oluştur\r\n            INSERT INTO public.cari_hesap_hareketleri (\r\n                cari_hesap_id,\r\n                hareket_tipi,\r\n                tutar,\r\n                onceki_bakiye,\r\n                sonraki_bakiye,\r\n                aciklama,\r\n                belge_tipi,\r\n                belge_no,\r\n                referans_id\r\n            ) VALUES (\r\n                NEW.cari_hesap_id,\r\n                'alacak',\r\n                NEW.toplam_tutar,\r\n                current_balance,\r\n                current_balance - NEW.toplam_tutar,\r\n                'İade - ' || NEW.iade_no,\r\n                'iade',\r\n                NEW.iade_no,\r\n                NEW.id\r\n            );\r\n            \r\n            -- Cari hesap bakiyesini güncelle\r\n            UPDATE public.cari_hesaplar \r\n            SET bakiye = current_balance - NEW.toplam_tutar,\r\n                guncelleme_tarihi = now()\r\n            WHERE id = NEW.cari_hesap_id;\r\n        END IF;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$$ LANGUAGE plpgsql","-- İade trigger'ı oluştur\r\nDROP TRIGGER IF EXISTS trigger_iade_movements ON public.iadeler","CREATE TRIGGER trigger_iade_movements\r\n    AFTER INSERT OR UPDATE ON public.iadeler\r\n    FOR EACH ROW\r\n    EXECUTE FUNCTION public.create_iade_movements()","-- İade numarası otomatik oluşturma function'ı\r\nCREATE OR REPLACE FUNCTION public.generate_iade_no()\r\nRETURNS TRIGGER AS $$\r\nDECLARE\r\n    next_number INTEGER;\r\n    formatted_number TEXT;\r\nBEGIN\r\n    IF NEW.iade_no IS NULL OR NEW.iade_no = '' THEN\r\n        -- Bugünkü iade sayısını al\r\n        SELECT COALESCE(COUNT(*), 0) + 1 INTO next_number\r\n        FROM public.iadeler \r\n        WHERE DATE(iade_tarihi) = DATE(NEW.iade_tarihi);\r\n        \r\n        -- Format: IAD-YYYYMMDD-XXX\r\n        formatted_number := 'IAD-' || TO_CHAR(NEW.iade_tarihi, 'YYYYMMDD') || '-' || LPAD(next_number::TEXT, 3, '0');\r\n        NEW.iade_no := formatted_number;\r\n    END IF;\r\n    \r\n    RETURN NEW;\r\nEND;\r\n$$ LANGUAGE plpgsql","-- İade numarası trigger'ı\r\nDROP TRIGGER IF EXISTS trigger_generate_iade_no ON public.iadeler","CREATE TRIGGER trigger_generate_iade_no\r\n    BEFORE INSERT ON public.iadeler\r\n    FOR EACH ROW\r\n    EXECUTE FUNCTION public.generate_iade_no()","-- Debug için stok düzeylerini kontrol eden function\r\nCREATE OR REPLACE FUNCTION public.check_stock_levels()\r\nRETURNS TABLE (\r\n    stok_id UUID,\r\n    stok_adi TEXT,\r\n    eldeki_miktar INTEGER,\r\n    stok_durumu TEXT\r\n) AS $$\r\nBEGIN\r\n    RETURN QUERY\r\n    SELECT s.id, s.stok_adi, s.eldeki_miktar, s.stok_durumu\r\n    FROM public.stoklar s\r\n    ORDER BY s.stok_adi;\r\nEND;\r\n$$ LANGUAGE plpgsql"}	fix_stock_deduction	\N	\N
