--
-- Name: v_onay_bekleyen_siparisler _RETURN; Type: RULE; Schema: public; Owner: -
--

CREATE OR REPLACE VIEW public.v_onay_bekleyen_siparisler AS
 SELECT sd.id,
    sd.siparis_id,
    sd.siparis_numarasi,
    sd.musteri_adi,
    sd.toplam_tutar,
    sd.onay_durumu,
    sd.adet_uyusmazligi,
    sd.duzenleme_sayisi,
    sd.yeniden_kontrol_sayisi,
    count(sau.id) AS uyusmazlik_sayisi,
    sd.created_at,
    sd.updated_at
   FROM (public.siparis_durumlari sd
     LEFT JOIN public.siparis_adet_uyusmazliklari sau ON ((sd.id = sau.siparis_durum_id)))
  WHERE ((sd.onay_durumu = 'onay_bekliyor'::text) AND (sd.durum = 'kontrol'::text))
  GROUP BY sd.id;


